WITH monthly_values AS ( SELECT _year, _month, index_value FROM interest_metrics WHERE interest_id = (SELECT id FROM interest_map WHERE interest_name = 'Data Science') ), lagged AS ( SELECT mv._year, mv._month, mv.index_value, LAG(mv.index_value) OVER (ORDER BY mv._year, mv._month) AS prev_value FROM monthly_values mv ) SELECT _year, _month, (index_value - prev_value) * 100.0 / NULLIF(prev_value, 0) AS growth_rate FROM lagged WHERE prev_value IS NOT NULL;