SELECT vc.item_name, SUM(vt.`qty_sold(kg)` * (vt.`unit_selling_px_rmb/kg` * (1 - vl.`loss_rate_%` / 100.0) - vw.`whsle_px_rmb-kg`)) AS profit FROM veg_txn_df vt JOIN veg_cat vc ON vt.item_code = vc.item_code JOIN veg_whsle_df vw ON vt.item_code = vw.item_code AND vt.txn_date = vw.whsle_date JOIN veg_loss_rate_df vl ON vt.item_code = vl.item_code GROUP BY vc.item_name ORDER BY profit DESC LIMIT 5;