sample_rows.interventions:
[
  {
    "arm_group_labels": "Enhanced Usual Care (EUC), Intervention",
    "description": "In the proposed study, we will conduct a randomized clinical trial of low-income, adult Latino(a)s with type 2 diabetes randomized at the individual level to TIME (Telehealth-supported, Integrated CHWs, Medication-access, group visit Education) (intervention) versus usual care enhanced with education (EUC). Our research team will provide telementoring to local clinic teams (providers and CHWs) to initiate the 12-month TIME program into their clinics.",
    "name": "Intervention",
    "type": "Other"
  }
]

sample_rows.study_arms:
[
  {
    "description": "Intervention individuals will receive the 12-month diabetes TIME program",
    "label": "Intervention",
    "type": "Experimental"
  },
  {
    "description": "We will define EUC as usual clinical care, which consists of diabetes management provider encounters (mean quarterly) plus monthly individual appointments with a pharm-Ds or nutritionists as clinically indicated. In addition, clinics offer multiple other individual and group opportunities. In addition for the EUC arm, we will provide three quarterly, 1-hour classes (nutrition medications, exercise). The classes will be led by a research-staff trained, bilingual healthcare professional e.g., nurse. EUC individuals will not have contact with CHWs.",
    "label": "Enhanced Usual Care (EUC)",
    "type": "Other"
  }
]

